// DILITHIUM VERIFICATION
// ================================================================================================
pub fn prove(
    z: [[BaseElement; N]; K],
    w: [[BaseElement; N]; K],
    qw: [[BaseElement; N]; K],
    ctilde: [BaseElement; HASH_DIGEST_WIDTH],
    m: [BaseElement; 12],
    com_r: [BaseElement; 12]
) -> StarkProof {
    // 48,4,20
    // 32,8,20
    // 24,16,20
    // 19,32,21
    // 16,64,20
    // 14,128,20
    let options = ProofOptions::new(
        48, // number of queries
        4,  // blowup factor
        20,  // grinding factor
        HashFunction::Blake3_256,
        FieldExtension::Sextic,
        8,   // FRI folding factor
        128, // FRI max remainder length
    );

    debug!(
        "Generating proof for correctness of Dilithium
        ---------------------"
    );

    // create a prover
    let prover = ThinDilProver::new(options.clone(), z, w, qw, ctilde, m, com_r);

    // generate execution trace
    let now = Instant::now();
    let trace = prover.build_trace();

    let trace_width = trace.width();
    let trace_length = trace.length();
    debug!(
        "Generated execution trace of {} registers and 2^{} steps in {} ms",
        trace_width,
        log2(trace_length),
        now.elapsed().as_millis()
    );

    // generate the proof
    prover.prove(trace).unwrap()
}

pub fn verify(proof: StarkProof, m: [BaseElement; HASH_DIGEST_WIDTH]) -> Result<(), VerifierError> {
    let pub_inputs = PublicInputs{m};
    winterfell::verify::<ThinDilAir>(proof, pub_inputs)
}

pub fn verify_with_wrong_inputs(proof: StarkProof, m: [BaseElement; HASH_DIGEST_WIDTH]) -> Result<(), VerifierError> {
    let pub_inputs = PublicInputs{m};
    winterfell::verify::<ThinDilAir>(proof, pub_inputs)
}

